CREATE TABLE IF NOT EXISTS `ims_wx_tuijian` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `title` varchar(100) NOT NULL DEFAULT '' COMMENT '公众号名称', `description` varchar(100) NOT NULL DEFAULT '' COMMENT '公众号名称', `guanzhuUrl` varchar(255) NOT NULL DEFAULT '' COMMENT '引导关注', `type` varchar(1) NOT NULL DEFAULT '1', `clickNum` int(10) unsigned NOT NULL DEFAULT '0', `ipclient` varchar(50) NOT NULL DEFAULT '' COMMENT 'ip', `thumb` varchar(500) NOT NULL DEFAULT '' COMMENT '缩略图', `createtime` int(10) unsigned NOT NULL DEFAULT '0', r `hot` int(1) NOT NULL COMMENT '是否热门 0默认 1热门', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8; CREATE TABLE IF NOT EXISTS `ims_fineness_article` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `title` varchar(100) NOT NULL DEFAULT '', `musicurl` varchar(100) NOT NULL DEFAULT '' COMMENT '上传音乐', `content` text NOT NULL, `credit` varchar(255) DEFAULT '0', `pcate` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '一级分类', `ccate` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '二级分类', `template` VARCHAR(300) NOT NULL DEFAULT '' COMMENT '内容模板目录', `templatefile` VARCHAR(300) NOT NULL DEFAULT '' COMMENT '分类模板名称', `bg_music_switch` varchar(1) NOT NULL DEFAULT '1', `clickNum` int(10) unsigned NOT NULL DEFAULT '0', `thumb` varchar(500) NOT NULL DEFAULT '' COMMENT '缩略图', `description` varchar(500) NOT NULL DEFAULT '' COMMENT '简介', `createtime` int(10) unsigned NOT NULL DEFAULT '0', `displayorder` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `outLink` varchar(500) DEFAULT '0' COMMENT '外链', `author` varchar(100) DEFAULT '' COMMENT '作者', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_fineness_article_category` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属帐号', `name` varchar(50) NOT NULL COMMENT '分类名称', `parentid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上级分类ID,0为第一级', `displayorder` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `thumb` varchar(1024) NOT NULL DEFAULT '' COMMENT '分类图片', `description` varchar(100) NOT NULL DEFAULT '' COMMENT '分类描述', `template` VARCHAR(300) NOT NULL DEFAULT '' COMMENT '分类模板目录', `templatefile` VARCHAR(300) NOT NULL DEFAULT '' COMMENT '分类模板名称', `createtime` int(10) unsigned NOT NULL DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE if not exists `ims_fineness_adv` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) default 0, `link` varchar(255) DEFAULT '', `title` varchar(255) DEFAULT '', `thumb` varchar(255) DEFAULT '', PRIMARY KEY (`id`),KEY `indx_weid` (`weid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='幻灯片';
CREATE TABLE IF NOT EXISTS `ims_fineness_sysset`( `id` int(11) AUTO_INCREMENT, `weid` int(11) DEFAULT 0 , `guanzhuUrl` varchar(255) DEFAULT '' comment '引导关注', `guanzhutitle` varchar(255) DEFAULT '' comment '引导关注名称', `historyUrl` varchar(255) DEFAULT '' comment '历史记录外链', `copyright` varchar(255) DEFAULT '' comment '版权', `cnzz` varchar(255) DEFAULT '' comment '统计', `appid` varchar(255) default '', `logo` varchar(255) default '', `appsecret` varchar(255) default '', `appid_share` varchar(255) default '', `tjgzh` varchar(255) DEFAULT '1' comment '推荐公众号图片', `tjgzhUrl` varchar(255) DEFAULT '1' comment '推荐公众号引导关注', `appsecret_share` varchar(255) default '', PRIMARY KEY (`id`),KEY `indx_weid` (`weid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
